import java.io.ObjectOutputStream;import java.io.FileOutputStream;import java.io.IOException;import java.io.Serializable;class A implements Serializable {    public B b;    public C c;}class B implements Serializable {    transient private int x = 3;    private String s = "un";    public B (int x, String s) {        this.x = x; this.s = s;    }    public String toString()   { return s + "/" + x; }}class C implements Serializable {    public int i;    public A a;    public C suiv;    public C (int i, A a, C suiv) { this.i = i; this.a = a; this.suiv = suiv; }}public class Ecrivain {    public static void main (String args[]) throws IOException    {        A a = new A();        a.b = new B(123, "coucou");        a.c = new C (28, null, new C (76, a, null));        System.out.println ("B = " + a.b);        System.out.println ("C1.i = " + a.c.i);        System.out.println ("C2.i = " + a.c.suiv.i);        System.out.println ("C3.i = " + a.c.suiv.a.c.i);        ObjectOutputStream flot = new ObjectOutputStream(new FileOutputStream("/tmp/sauve"));        flot.writeObject (a);        flot.close();    }}